logic.script:
const lastPush = ledger.findLast({ topic: 'IMPLEMENTATION_READY' });
if (!lastPush) return false;
const response = ledger.findLast({ topic: 'VALIDATION_RESULT', since: lastPush.timestamp });
return response?.content?.data?.approved === false || response?.content?.data?.approved === 'false';

